  /* Attach tracking to all download & external links */
var _gaq = _gaq || [];

function _gaLt(event){
    var el = event.srcElement || event.target;

    /* Loop through parent elements if clicked element is not a link (ie:  */
    while(el && (typeof el.tagName == 'undefined' || el.tagName.toLowerCase() != 'a' || !el.href))
        el = el.parentNode;

    if(el && el.href){
        dl = document.location;
        l = dl.pathname + dl.search;
        h = el.href;
        c = !1;
        if(h.indexOf(location.host) == -1){
            c = "Outgoing Links";
            a = h;
        }
        else if(h.match(/\/assets\//) && !h.match(/\.(jpe?g|bmp|png|gif|tiff?)$/i)){
            c = "Downloads";
            a = h.match(/\/assets\/(.*)/)[1];
        }
        if(c){
            _gaq.push(["_trackEvent",c,a,l]);
            /* Push secondary tracker if set */
            _gaq2 && _gaq.push(["b._trackEvent",c,a,l]);
            /* if target not set delay opening of window by 0.5s to allow tracking */
            if(!el.target || el.target.match(/^_(self|parent|top)$/i)){
                setTimeout(function(){
                    document.location.href = el.href;
                }.bind(el),500);
                /* Prevent standard click */
                event.preventDefault ? event.preventDefault() : event.returnValue = !1;
            }
        }

    }
}

var d = document;
d.addEventListener ? d.addEventListener("click",_gaLt,!1) : d.attachEvent && d.attachEvent("onclick",_gaLt);

$(document).ready(function(){
    $("#wwamapImage").click(function(e){
        var offset_l = $(this).offset().left - $(window).scrollLeft();
        var offset_t = $(this).offset().top - $(window).scrollTop();
        var left =  Math.round( (e.clientX - offset_l) );
        var top = Math.round( (e.clientY - offset_t) );
        //console.log("click position result --> X: " + left + ", Y: " + top);
        $('#mapx').val(left);
        $('#mapy').val(top);
    });
});
